using System;

namespace AttributeFormatting
{
	[AttributeUsage(AttributeTargets.Class)]
	public class TestAttribute : Attribute
	{
		protected int[] mIntArray = null;
		protected string mString = string.Empty;
		protected byte mByte = 0;
		public string mPublicString = string.Empty;

		public TestAttribute() : base() {}

		public TestAttribute(string stringData, int[] intData) : base()
		{
			this.mIntArray = intData;
			this.mString = stringData;
		}

		public byte ByteData
		{
			get
			{
				return this.mByte;
			}
			set
			{
				this.mByte = value;
			}
		}

		public string StringData
		{
			get
			{
				return this.mString;
			}
		}

		public int[] IntegerData
		{
			get
			{
				return this.mIntArray;
			}
		}
	}

	[TestAttribute("My string data.", new int[] {2, 3}, 
		 ByteData = 22)]
	public class TestClass
	{

	}
		
	// 16000 bytes long.
	[TestAttribute("" + 
		"" + 
		"" + 
		"" + 
		"" + 
		"" + 
		"" +  
		"",
		new int[] {2, 3}, 
		ByteData = 22)]
	public class LongStringTestClass
	{

	}

	[TestAttribute(mPublicString = "22")]
	public class AnotherTestClass
	{

	}

	[TestAttribute()]
	public class YetAnotherTestClass
	{

	}
}
